mapper.select:
SELECT CONCAT_WS(" ", e.first_name, e.last_name) AS fullname, e.email, DATE_FORMAT(CONVERT_TZ(modified_date, "UTC", a.offset_timezone),"%Y-%m-%d %H:%i:%s") AS `timestamp`, a.id, a.action_mode, a.description, a.offset_timezone, a.modified_date, a.modified_page, a.id_employee, a.id_site FROM auditing_logs a LEFT JOIN employee e ON e.id = a.id_employee WHERE e.`status` = 1 AND e.is_delete = 0 AND e.id_company = #{id_company}
AND a.modified_date BETWEEN CONVERT_TZ(#{date_from}, 'UTC', a.offset_timezone) AND CONVERT_TZ(#{date_to}, 'UTC', a.offset_timezone)
AND a.action_mode = #{action_mode}
AND (CONCAT_WS(' ', e.first_name, e.last_name) LIKE CONCAT("%",#{keyword}, "%") OR CONCAT_WS(' ', e.last_name, e.first_name) LIKE CONCAT("%",#{keyword}, "%"))
order by a.id ASC
LIMIT ${limit} OFFSET ${offset};

SELECT count(*) as totalRecord FROM auditing_logs a LEFT JOIN employee e ON e.id = a.id_employee WHERE e.`status` = 1 AND e.is_delete = 0 AND e.id_company = #{id_company}
AND a.modified_date BETWEEN CONVERT_TZ(#{date_from}, 'UTC', a.offset_timezone) AND CONVERT_TZ(#{date_to}, 'UTC', a.offset_timezone)
AND a.action_mode = #{action_mode}
AND (CONCAT_WS(' ', e.first_name, e.last_name) LIKE CONCAT("%",#{keyword}, "%") OR CONCAT_WS(' ', e.last_name, e.first_name) LIKE CONCAT("%",#{keyword}, "%"))
order by a.id ASC

SELECT s.id, s.`name`, s.`description`, s.`status`, s.is_delete, s.order_id, s.icon, 0 AS is_expand, s.id_company, s.parent, c.name AS company_name, COUNT(sm.id_site) AS total_sites, IF( COUNT(sm.id_site) = 0, JSON_ARRAY(), JSON_ARRAYAGG(JSON_OBJECT( 'id', ss.id, 'id_hash', SHA1(s.id), 'name', ss.name, 'id_company', ss.id_company, 'text', ss.`name`, 'label', ss.`name`, 'value', ss.id )) ) AS sites
FROM site_group s LEFT JOIN company c ON c.id = s.id_company LEFT JOIN site_group_map sm ON sm.id_site_group = s.id LEFT JOIN site ss ON ss.id = sm.id_site WHERE s.status = 1 AND s.is_delete = 0 AND s.id_company = #{id_company}
AND s.name LIKE CONCAT("%",#{keyword}, "%")
GROUP BY s.id
order by
c.name ${order_by}
s.${sort_column} ${order_by}
s.id, s.parent DESC
LIMIT ${limit} OFFSET ${offset};

mapper.insert:
INSERT INTO `site_sub_group`( `name`, `id_site_group`, `description`, `order_id`, `id_company`, `icon`
)VALUES( #{name}, #{id}, #{description}, #{order_id}, #{id_company}, #{icon} );
SELECT LAST_INSERT_ID() as id
