SELECT
	COUNT(l.id) as totalRecord

FROM
	`alert` l
	LEFT JOIN error e ON e.id = l.id_error
	LEFT JOIN error_level el ON el.id = e.id_error_level
	LEFT JOIN device d ON d.id = l.id_device
	LEFT JOIN site s ON s.id = d.id_site
	LEFT JOIN time_zone t ON t.id = s.id_time_zone

	WHERE  e.`status` = 1 AND e.is_delete = 0 AND d.`status` = 1 AND d.is_delete = 0
	AND l.is_delete = 0 
	AND s.enable_alert = 1

		AND l.`status` = 1
		AND l.resolved = 0



		AND l.`status` = 0



		AND CONVERT_TZ( l.start_date, '+00:00', t.`offset` )  BETWEEN DATE_FORMAT( #{date_from} ,'%Y-%m-%d %H:%i')  AND DATE_FORMAT( #{date_to} ,'%Y-%m-%d %H:%i')


		AND s.id IN  (

				#{item.id}

		)



		AND el.id IN  (

				#{item.id}

		)



		AND s.`name` LIKE CONCAT("%",#{site_name}, "%")



		AND l.is_notification = 0



		AND l.status IN  (

				#{item.id}

		)



		AND e.id_error_type IN  (

				#{item.id}

		)



		AND d.id_device_type IN  (

				#{item.id}

		)





		AND CONVERT_TZ( l.start_date, '+00:00', t.`offset` )  BETWEEN #{date_from}  AND #{date_to}

SELECT l.id, l.asset, l.capacity, l.`status`, l.alert_acknowledged, l.disable_notification, l.resolved, l.note, IF(l.`status` = 1, 'Open', 'Close') AS status_name, al.`name` AS `level`, e.message, e.error_code, d.devicename, s.`name` AS site_name, s.id AS id_site, d.id as id_device, 'kW' AS capacity_unit, DATE_FORMAT( CONVERT_TZ( l.start_date, t.`offset`, #{offset_timezone} ), #{format_sql_long} ) AS start, DATE_FORMAT( CONVERT_TZ( l.start_date,'+00:00', t.`offset`) , '%m/%d/%Y %H:%i %p') AS start_date, DATE_FORMAT(CONVERT_TZ( l.end_date,'+00:00', t.`offset`), '%m/%d/%Y %H:%i %p') AS end_date, DATE_FORMAT(CONVERT_TZ( d.last_updated,'+00:00', t.`offset`), '%m/%d/%Y %H:%i %p') AS last_updated, CONCAT_WS('', IF( FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / (60*24))) < 0 , 0, FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time}) / (60*24)))), 'd ', IF(MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / 60)), 24) < 0, 0, MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / 60)), 24)), 'h ', IF( MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time})), 60) < 0, 0, MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time})), 60)), 'm' ) AS duration
FROM `alert` l LEFT JOIN alert_level al ON al.id = l.id_level LEFT JOIN error e ON e.id = l.id_error LEFT JOIN device d ON d.id = l.id_device
LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE s.enable_alert = 1 AND s.id_customer = #{id_customer} AND s.id = #{id_site} AND l.id = #{id}
LIMIT 1;

SELECT s.id, s.`name`, s.`name` AS text, 1 AS active FROM site s WHERE s.is_delete = 0 AND s.`status` = 1
AND s.id IN (
#{item.id}
)
ORDER BY s.`name` ASC

SELECT COUNT(l.id) as totalRecord
FROM `alert` l LEFT JOIN error e ON e.id = l.id_error LEFT JOIN error_level el ON el.id = e.id_error_level LEFT JOIN device d ON d.id = l.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE e.`status` = 1 AND e.is_delete = 0 AND s.enable_alert = 1
AND l.status = 1 AND l.resolved = 0
AND l.status = 0
AND CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) BETWEEN #{date_from} AND #{date_to}
AND s.id IN (
#{item.id}
)
AND el.id IN (
#{item.id}
)
AND s.`name` LIKE CONCAT("%",#{site_name}, "%")

SELECT COUNT( DISTINCT a.id ) AS totalRecord FROM site s LEFT JOIN site_customer_map scm ON s.id = scm.id_site LEFT JOIN device d ON d.id_site = s.id LEFT JOIN alert a ON a.id_device = d.id WHERE
s.`status` = 1 AND s.is_delete = 0 AND a.`status` = 1 AND a.is_delete = 0 AND d.`status` = 1 AND d.is_delete = 0 AND s.enable_alert = 1
AND s.id IN (
#{item.id}
)
AND scm.id_customer = #{id_customer}

SELECT l.id, l.asset, el.name AS priority_name, l.capacity, l.`status`, l.alert_acknowledged, l.disable_notification, l.resolved, l.note, IF(l.`status` = 1, 'Open', 'Close') AS status_name, el.`name` AS `level`, e.message, e.error_code, d.devicename, s.`name` AS site_name, s.id AS id_site, d.id as id_device, i.icon AS icon_alert, d.datatablename, d.view_tablename, d.job_tablename, d.id AS id_device, DATE_FORMAT( l.start_date, '%Y-%m-%d %H:%i:%s') AS org_start_date, el.id AS id_error_level, DATE_FORMAT( CONVERT_TZ( l.start_date, t.`offset`, #{offset_timezone} ), #{format_sql_long} ) AS start, DATE_FORMAT( CONVERT_TZ( l.start_date,'+00:00', t.`offset`) , '%m/%d/%Y %H:%i %p') AS start_date, DATE_FORMAT(CONVERT_TZ( l.end_date,'+00:00', t.`offset`), '%m/%d/%Y %H:%i %p') AS end_date, DATE_FORMAT(CONVERT_TZ( d.last_updated,'+00:00', t.`offset`), '%m/%d/%Y %H:%i %p') AS last_updated, CONCAT_WS('', IF( FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / (60*24))) < 0 , 0, FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time}) / (60*24)))), 'd ', IF(MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / 60)), 24) < 0, 0, MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / 60)), 24)), 'h ', IF( MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time})), 60) < 0, 0, MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time})), 60)), 'm' ) AS duration, IFNULL( FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time}))) , 0) AS open_period
FROM `alert` l LEFT JOIN error e ON e.id = l.id_error LEFT JOIN error_level el ON el.id = e.id_error_level LEFT JOIN icon i ON i.id = el.id_icon LEFT JOIN device d ON d.id = l.id_device
LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE e.`status` = 1 AND e.is_delete = 0 AND s.id = #{id_site} AND l.is_delete = 0 AND s.enable_alert = 1
AND l.status = 1
AND l.status = 0
AND CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) BETWEEN #{date_from} AND #{date_to}
AND el.id IN (
#{item.id}
)
AND s.`name` LIKE CONCAT("%",#{site_name}, "%")
GROUP BY l.id
ORDER BY l.`id` ${order_by}
ORDER BY s.`name` ${order_by}
ORDER BY el.`level` ${order_by}
ORDER BY e.`message` ${order_by}
ORDER BY d.`devicename` ${order_by}
ORDER BY l.`start_date` ${order_by}
ORDER BY l.`end_date` ${order_by}
ORDER BY `open_period` ${order_by}
ORDER BY id DESC

SELECT
