	AND s.`id_employee` = #{id_employee} 
	LIMIT 1;

SELECT ( SELECT COUNT( e.id_error_level ) AS lowPriority FROM alert a LEFT JOIN device d ON d.id = a.id_device LEFT JOIN error e ON a.id_error = e.id LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE e.id_error_level = 13 AND a.`status` = 1 AND e.`status` = 1 AND a.is_delete = 0 AND e.is_delete = 0 AND d.`status` = 1 AND d.is_delete = 0 AND s.enable_alert = 1 AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL)
AND d.id_site IN (
#{item.id}
)
GROUP BY e.id_error_level ) AS lowPriority,
( SELECT COUNT( e.id_error_level ) AS highPriority FROM alert a LEFT JOIN error e ON a.id_error = e.id LEFT JOIN device d ON d.id = a.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE e.id_error_level = 11 AND a.`status` = 1 AND e.`status` = 1 AND a.is_delete = 0 AND e.is_delete = 0 AND d.`status` = 1 AND d.is_delete = 0 AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL)
AND d.id_site IN (
#{item.id}
)
GROUP BY e.id_error_level ) AS highPriority,
( SELECT COUNT(*) AS totalError FROM alert a LEFT JOIN error e ON a.id_error = e.id LEFT JOIN device d ON d.id = a.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE a.`status` = 1 AND e.`status` = 1 AND a.is_delete = 0 AND e.is_delete = 0 AND d.`status` = 1 AND d.is_delete = 0 AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL)
AND d.id_site IN (
#{item.id}
)
) AS totalError

SELECT s.id, SHA1(s.id) AS hash_id, s.`name` AS site_name, s.dc_capacity, s.ac_capacity, SUM(if(d.id_device_type = 1, 1, 0)) AS totalInverter, SUM(if(d.id_device_type = 3, 1, 0)) AS totalMeter, IFNULL(SUM(if(d.id_device_type = 3, d.last_value, 0)), SUM(if(d.id_device_type = 1, d.last_value, 0))) AS power, l.last_updated, h.is_hiding, IFNULL(g.alertMeter, 0) AS alertMeter, IFNULL(g.alertInverter, 0) AS alertInverter, IFNULL(g.totalAlert, 0) AS totalAlert, al.start_date FROM site s LEFT JOIN device d ON d.id_site = s.id LEFT JOIN site_employee_map sem ON sem.id_site = s.id LEFT JOIN time_zone t ON t.id = s.id_time_zone
LEFT JOIN ( SELECT id_site, MAX(is_hiding) AS is_hiding FROM site_employee_map GROUP BY id_site ) h ON h.id_site = s.id
LEFT JOIN ( SELECT s.id, a.start_date FROM alert a LEFT JOIN device d ON d.id = a.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN site_employee_map sem ON sem.id_site = s.id AND sem.id_employee = #{id_employee}
WHERE a.`status` = 1 AND d.`status` = 1 AND s.`status` = 1 AND s.enable_alert = 1 GROUP BY d.id_site ORDER BY a.start_date DESC )al ON al.id = s.id
LEFT JOIN ( SELECT s.id, DATE_FORMAT(CONVERT_TZ( d.last_updated, '+00:00', tz.`offset` ), '%m/%d/%Y %H:%i:%s %p') AS last_updated FROM device d LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone tz ON tz.id = s.id_time_zone LEFT JOIN site_employee_map sem ON sem.id_site = s.id AND sem.id_employee = #{id_employee} WHERE d.id_device_type = 5 AND s.`status` = 1 AND s.is_delete = 0 AND d.`status` = 1 AND d.`is_delete` = 0 GROUP BY s.id )l ON l.id = s.id
LEFT JOIN ( SELECT s.id, SUM(if(d.id_device_type = 3, 1, 0)) AS alertMeter, SUM(if(d.id_device_type = 1, 1, 0)) AS alertInverter, COUNT(a.id) AS totalAlert FROM alert a LEFT JOIN error e ON e.id = a.id_error LEFT JOIN error_level el ON el.id = e.id_error_level LEFT JOIN device d ON d.id = a.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN site_employee_map sem ON sem.id_site = s.id AND sem.id_employee = #{id_employee} LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE a.is_delete = 0 AND e.`status` = 1 AND e.`is_delete` = 0
AND a.`status` = 1
AND a.status IN (
#{item.id}
)
AND s.id IN (
#{item.id}
)
AND el.id IN (
#{item.id}
)
AND e.id_error_type IN (
#{item.id}
)
AND d.id_device_type IN (
#{item.id}
)
AND CONVERT_TZ( a.start_date, '+00:00', t.`offset` ) BETWEEN #{date_from} AND #{date_to}
GROUP BY s.id )g ON g.id = s.id WHERE s.`status` = 1 AND s.enable_alert = 1 AND s.is_delete = 0 AND d.`status` = 1 AND d.is_delete = 0 AND totalAlert > 0 AND sem.id_employee = #{id_employee} AND sem.is_hiding = 0 AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL)
AND s.`name` LIKE CONCAT("%",#{site_name}, "%")
GROUP BY s.id
ORDER BY s.`name` ${order_by}
ORDER BY g.`totalAlert` ${order_by}
ORDER BY g.`alertInverter` ${order_by}
ORDER BY g.`alertMeter` ${order_by}
ORDER BY s.dc_capacity ${order_by}
ORDER BY `power` ${order_by}
ORDER BY d.`last_updated` ${order_by}
ORDER BY al.start_date DESC
LIMIT #{limit} OFFSET #{offset};

SELECT COUNT(s.id) AS totalRecord FROM site s LEFT JOIN site_employee_map sem ON sem.id_site = s.id
LEFT JOIN ( SELECT s.id, SUM(if(d.id_device_type = 3, 1, 0)) AS alertMeter, SUM(if(d.id_device_type = 1, 1, 0)) AS alertInverter, COUNT(a.id) AS totalAlert FROM alert a LEFT JOIN device d ON d.id = a.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN site_employee_map sem ON sem.id_site = s.id AND sem.id_employee = #{id_employee} LEFT JOIN time_zone t ON t.id = s.id_time_zone LEFT JOIN error e ON e.id = a.id_error LEFT JOIN error_level el ON el.id = e.id_error_level WHERE a.is_delete = 0 AND e.`status` = 1 AND e.`is_delete` = 0 AND s.enable_alert = 1
AND a.`status` = 1
AND a.status IN (
#{item.id}
)
AND s.id IN (
#{item.id}
)
AND el.id IN (
#{item.id}
)
AND e.id_error_type IN (
#{item.id}
)
AND d.id_device_type IN (
#{item.id}
)
AND CONVERT_TZ( a.start_date, '+00:00', t.`offset` ) BETWEEN #{date_from} AND #{date_to}
GROUP BY s.id )g ON g.id = s.id WHERE s.`status` = 1 AND s.is_delete = 0 AND sem.id_employee = #{id_employee} AND sem.is_hiding = 0 AND totalAlert > 0
AND s.`name` LIKE CONCAT("%",#{site_name}, "%")

SELECT e.alert_per_page
FROM employee e
WHERE e.id = #{id_employee} LIMIT 1;
