SELECT l.id, l.asset, l.capacity, l.`status`, l.alert_acknowledged, l.disable_notification, l.resolved, l.note, IF(l.`status` = 1, 'Open', 'Close') AS status_name, al.`name` AS `level`, e.message, e.id AS id_error, e.error_code, e.troubleshoot_enable, d.devicename, dg.group_name, dg.manufacturer_phone, dg.manufacturer_email, dg.manufacturer_name, AS site_name, s.id AS id_site, d.id as id_device, d.serialnumber, 'kW' AS capacity_unit, DATE_FORMAT( CONVERT_TZ( l.start_date, t.`offset`, #{offset_timezone} ), #{format_sql_long} ) AS start, DATE_FORMAT( CONVERT_TZ( l.start_date,'+00:00', t.`offset`) , '%m/%d/%Y %H:%i %p') AS start_date, DATE_FORMAT(CONVERT_TZ( l.end_date,'+00:00', t.`offset`), '%m/%d/%Y %H:%i %p') AS end_date, DATE_FORMAT(CONVERT_TZ( d.last_updated,'+00:00', t.`offset`), '%m/%d/%Y %H:%i %p') AS last_updated, CONCAT_WS('', IF( FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / (60*24))) < 0 , 0, FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time}) / (60*24)))), 'd ', IF(MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / 60)), 24) < 0, 0, MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / 60)), 24)), 'h ', IF( MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time})), 60) < 0, 0, MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time})), 60)), 'm' ) AS duration
FROM `alert` l LEFT JOIN alert_level al ON al.id = l.id_level LEFT JOIN error e ON e.id = l.id_error LEFT JOIN device d ON d.id = l.id_device LEFT JOIN device_group dg ON d.id_device_group = dg.id
LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE s.enable_alert = 1 AND s.id_customer = #{id_customer} AND s.id = #{id_site} AND l.id = #{id}
LIMIT 1;

