SELECT l.id, l.asset, el.name AS priority_name, l.capacity, l.`status`, l.alert_acknowledged, l.disable_notification, l.resolved, l.note, IF(l.`status` = 1, 'Open', 'Close') AS status_name, el.`name` AS `level`, e.message, e.id AS id_error, e.error_code, e.troubleshoot_enable, d.devicename, AS site_name, s.id AS id_site, d.id as id_device, i.icon AS icon_alert, d.datatablename, d.view_tablename, d.job_tablename, d.serialnumber, dg.group_name, dg.manufacturer_phone, dg.manufacturer_email, dg.manufacturer_name, d.id AS id_device, DATE_FORMAT( l.start_date, '%Y-%m-%d %H:%i:%s') AS org_start_date, el.id AS id_error_level, DATE_FORMAT( CONVERT_TZ( l.start_date, t.`offset`, #{offset_timezone} ), #{format_sql_long} ) AS start, DATE_FORMAT( CONVERT_TZ( l.start_date,'+00:00', t.`offset`) , '%m/%d/%Y %H:%i %p') AS start_date, DATE_FORMAT(CONVERT_TZ( l.end_date,'+00:00', t.`offset`), '%m/%d/%Y %H:%i %p') AS end_date, DATE_FORMAT(CONVERT_TZ( d.last_updated,'+00:00', t.`offset`), '%m/%d/%Y %H:%i %p') AS last_updated, CONCAT_WS('', IF( FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / (60*24))) < 0 , 0, FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time}) / (60*24)))), 'd ', IF(MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / 60)), 24) < 0, 0, MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / 60)), 24)), 'h ', IF( MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time})), 60) < 0, 0, MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time})), 60)), 'm' ) AS duration, IFNULL( FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time}))) , 0) AS open_period
FROM `alert` l LEFT JOIN error e ON e.id = l.id_error LEFT JOIN error_level el ON el.id = e.id_error_level LEFT JOIN icon i ON i.id = el.id_icon LEFT JOIN device d ON d.id = l.id_device LEFT JOIN device_group dg ON d.id_device_group = dg.id
