mapper.select:
		SELECT *
		FROM
			auditing_logs
		WHERE id_employee = #{id_employee}
		ORDER BY


                id ${order_by}


                modified_date ${order_by}


	            id DESC


		LIMIT ${limit} OFFSET ${offset}

SELECT al.id, CONCAT_WS( ' ', e.first_name,e.last_name) AS full_name, al.modified_page, al.modified_date, al.offset_timezone, al.action_mode, al.description, al.backup_item FROM auditing_logs al LEFT JOIN employee e ON e.id = al.id_employee WHERE al.id_site = #{id_site} ORDER BY
id ${order_by}
modified_date ${order_by}
id DESC
LIMIT ${limit} OFFSET ${offset}

    	SELECT count(*) as totalRecord
		FROM auditing_logs
		WHERE id_employee = #{id_employee}

    	SELECT count(*) as totalRecord
		FROM auditing_logs
		WHERE id_site = #{id_site}

SELECT al.*, concat( e.first_name, ' ', e.last_name ) AS `full_name`, e.table_column FROM auditing_logs al LEFT JOIN employee e ON e.id = al.id_employee WHERE
e.first_name LIKE CONCAT("%",#{keyword}, "%") OR e.last_name LIKE CONCAT("%",#{keyword}, "%")
ORDER BY
id ${order_by}
full_name ${order_by}
id DESC
LIMIT ${limit} OFFSET ${offset}
