mapper.select:
SELECT d.id, d.id_site, d.devicename, d.datatablename, IFNULL(d.meter_type, 0) AS meter_type, d.id_device_type, d.serialnumber, dg.table_name
FROM device AS d LEFT JOIN device_group dg ON dg.id = d.id_device_group WHERE d.is_delete = 0 AND d.`status` = 1 AND d.id_site = #{id_site}
AND d.id_device_group = 110
AND d.meter_type = #{meter_type}
GROUP BY d.id;

SELECT d.id, d.datatablename, d.devicename, IFNULL(d.energy_today, 0) AS energy_today, IFNULL(d.energy_yesterday, 0) AS energy_yesterday, IFNULL(d.energy_this_week, 0) AS energy_this_week, IFNULL(d.energy_last_week, 0) AS energy_last_week, IFNULL(d.energy_this_month, 0) AS energy_this_month, IFNULL(d.energy_last_month, 0) AS energy_last_month, IFNULL(d.energy_last_12_months, 0) AS energy_last_12_months, IFNULL(d.energy_compare_last_month, 0) AS energy_compare_last_month, IFNULL(d.energy_last30_days, 0) AS energy_last30_days, IFNULL(d.energy_this_year, 0) AS energy_this_year, IFNULL(d.energy_lifetime, 0) AS energy_lifetime, d.id_device_group, d.meter_type FROM device d WHERE d.id_site = #{id_site} AND d.`status` = 1 AND d.is_delete = 0
AND d.meter_type IN
#{id}

SELECT dv.* FROM ${datatablename} dv 
WHERE dv.time >= DATE_ADD( NOW() ,INTERVAL -30 MINUTE)  AND dv.id_device = #{id}
ORDER BY time DESC LIMIT 1;

SELECT ROUND( SUM(t.energy_today) ,2) AS energy_today, ROUND( SUM(t.energy_yesterday) ,2) AS energy_yesterday, ROUND( SUM(t.energy_this_week) ,2) AS energy_this_week, ROUND( SUM(t.energy_last_week) ,2) AS energy_last_week, ROUND( SUM(t.energy_this_month) ,2) AS energy_this_month, ROUND( SUM(t.energy_last_month) ,2) AS energy_last_month, ROUND( SUM(t.energy_last30_days) ,2) AS energy_last30_days, ROUND( SUM(t.energy_this_year) ,2) AS energy_this_year, ROUND( SUM(t.avg_daily_7days) ,2) AS avg_daily_7days, ROUND( SUM(t.avg_daily_last_7days) ,2) AS avg_daily_last_7days, ROUND( SUM(t.energy_compare_last_month) ,2) AS energy_compare_last_month FROM (
(SELECT d.id, d.energy_today, d.energy_yesterday, d.energy_this_week, d.energy_last_week, d.energy_this_month, d.energy_last_month, d.energy_last30_days, d.energy_this_year, d.avg_daily_7days, d.avg_daily_last_7days, d.energy_compare_last_month FROM device d WHERE d.`status` = 1 AND d.is_delete = 0 AND d.id = #{item.id})
)t

SELECT t.* FROM (
( SELECT dv.*, dg.table_name FROM ${item.datatablename} dv LEFT JOIN device d ON d.id = dv.id_device LEFT JOIN device_group dg ON dg.id = d.id_device_group WHERE time >= DATE_ADD(NOW(),INTERVAL -1 HOUR) ORDER BY time DESC LIMIT 1 )
) t

SELECT d.id, d.id_site, d.devicename, d.datatablename, IFNULL(d.meter_type, 0) AS meter_type, d.id_device_type, d.serialnumber
FROM device AS d
WHERE d.is_delete = 0 AND d.`status` = 1 AND d.id_site = #{id} GROUP BY d.id;

SELECT ROUND(AVG(t.nvmActiveEnergy), 1) AS water_avg_24h, ROUND(MAX(t.nvmActiveEnergy), 1) AS water_peak_24h FROM (
SELECT mi.time_format, mi.time_full, mi.categories_time, ROUND(IFNULL(LEAD(mi.nvmActiveEnergy) OVER (ORDER BY mi.time_format), ma.nvmActiveEnergy) - mi.nvmActiveEnergy, 2) AS nvmActiveEnergy FROM ( SELECT t.time_format, t.time_full, t.categories_time, dv.nvmActiveEnergy FROM ${item.datatablename} dv JOIN ( SELECT DATE_FORMAT( CONVERT_TZ( dv.time, 'UTC', t.`value` ), '%b %d, %Y %H:00' ) AS categories_time, DATE_FORMAT( CONVERT_TZ( dv.time, 'UTC', t.`value` ), '%Y-%m-%d %H:00' ) AS time_format, DATE_FORMAT( CONVERT_TZ( dv.time, 'UTC', t.`value` ), '%Y-%m-%d %H:00' ) AS time_full, MIN(dv.time) AS time FROM ${item.datatablename} dv LEFT JOIN device d ON d.id = dv.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE dv.time BETWEEN DATE_FORMAT( DATE_ADD(NOW(),INTERVAL -24 HOUR),"%Y-%m-%d 00:00:00" ) AND DATE_FORMAT( NOW(),"%Y-%m-%d 00:00:00" ) GROUP BY time_format ) t ON t.time = dv.time ) mi LEFT JOIN ( SELECT t.time_format, t.time_full, t.categories_time, dv.nvmActiveEnergy FROM ${item.datatablename} dv JOIN ( SELECT DATE_FORMAT( CONVERT_TZ( dv.time, 'UTC', t.`value` ), '%b %d, %Y %H:00' ) AS categories_time, DATE_FORMAT( CONVERT_TZ( dv.time, 'UTC', t.`value` ), '%Y-%m-%d %H:00' ) AS time_format, DATE_FORMAT( CONVERT_TZ( dv.time, 'UTC', t.`value` ), '%Y-%m-%d %H:00' ) AS time_full, MAX(dv.time) AS time FROM ${item.datatablename} dv LEFT JOIN device d ON d.id = dv.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE DATE_FORMAT( DATE_ADD(NOW(),INTERVAL -24 HOUR),"%Y-%m-%d 00:00:00" ) AND DATE_FORMAT( NOW(),"%Y-%m-%d 00:00:00" ) GROUP BY time_format ) t ON t.time = dv.time ) ma ON ma.time_full = mi.time_full
)t
