mapper.select:
SELECT s.id, SHA1(s.id) hash_id, s.id_country, s.id_time_zone, s.`name`, s.street, s.lat, s.lng, built_since, s.old_data, s.number, s.postal_code, s.city, s.state, commissioning, s.emergency_contact, s.ac_capacity, s.dc_capacity, s.status, s.is_delete, s.created_date, s.created_by, s.updated_date, s.updated_by, s.gallery, s.site_default, s.data_send_time, CONCAT_WS(', ',s.street, s.city, s.state, c.`name`, s.postal_code) AS address_full, scm.id_employee FROM site AS s LEFT JOIN country c ON c.id = s.id_country LEFT JOIN site_employee_map scm ON scm.id_site = s.id WHERE s.is_delete = 0 AND s.status = 1
AND scm.id_employee= #{id_employee}
AND s.id IN (
#{item.id}
)
GROUP BY s.id ORDER BY s.`name` ASC

SELECT COUNT(l.id) as totalRecord
FROM `alert` l LEFT JOIN error e ON e.id = l.id_error LEFT JOIN error_level el ON el.id = e.id_error_level LEFT JOIN device d ON d.id = l.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN site_employee_map scm ON s.id = scm.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE e.`status` = 1 AND e.is_delete = 0 AND scm.id_employee = #{id_customer} AND s.status = 1 AND s.is_delete = 0
AND l.status = 1
AND l.status = 0
AND CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) BETWEEN #{date_from} AND #{date_to}
AND s.id IN (
#{item.id}
)
AND el.id IN (
#{item.id}
)
AND s.`name` LIKE CONCAT("%",#{site_name}, "%")

SELECT COUNT( DISTINCT a.id ) AS totalRecord FROM site s LEFT JOIN site_employee_map scm ON s.id = scm.id_site LEFT JOIN device d ON d.id_site = s.id LEFT JOIN alert a ON a.id_device = d.id WHERE
s.`status` = 1 AND s.is_delete = 0 AND a.`status` = 1 AND a.is_delete = 0 AND d.`status` = 1 AND d.is_delete = 0 AND scm.id_employee = #{id_customer} AND scm.is_hiding = 0
AND s.id IN (
#{item.id}
)
