mapper.select:
SELECT s.id, SHA1(s.id) hash_id, s.id_country, s.id_time_zone, AS `name`, s.street, s.lat, s.lng, built_since, s.old_data, s.number, s.postal_code, s.city, s.state, DATE_FORMAT(s.commissioning, '%m/%d/%Y') AS commissioning, s.emergency_contact, s.ac_capacity, s.dc_capacity, s.status, s.is_delete, s.created_date, s.created_by, s.updated_date, s.updated_by, s.gallery, s.site_default, s.data_send_time, s.table_data_report, s.table_data_virtual, s.display_time_format, d.java_format AS java_date_format, TIME_FORMAT(CONVERT_TZ(s.sunrise, 'UTC', tz.`value`), '%H:%i:%s') AS sunrise, TIME_FORMAT(CONVERT_TZ(s.sunset, 'UTC', tz.`value`), '%H:%i:%s') AS sunset, tz.`value` AS timezone_value, CONCAT_WS(', ',s.street, s.city, s.state, c.`name`, s.postal_code) AS address_full FROM site AS s LEFT JOIN country c ON c.id = s.id_country LEFT JOIN site_employee_map scm ON scm.id_site = s.id LEFT JOIN time_zone tz ON tz.id = s.id_time_zone LEFT JOIN date_format d ON d.id = s.display_date_format WHERE s.is_delete = 0 AND s.status = 1
AND scm.id_employee= #{id_employee}
GROUP BY s.id ORDER BY s.`name` ASC

SELECT l.id, l.asset, el.name AS priority_name, l.capacity, l.`status`, l.alert_acknowledged, l.disable_notification, l.resolved, IF(l.`status` = 1, 'Open', 'Close') AS status_name, el.`name` AS `level`, e.message, e.error_code, d.devicename, AS site_name, s.id AS id_site, d.id as id_device, i.icon AS icon_alert, el.id AS id_error_level, dt.name AS device_type_name, el.color AS color_error_level, el.level, DATE_FORMAT( CONVERT_TZ( l.start_date, t.`offset`, #{offset_timezone} ), #{format_sql_long} ) AS start, DATE_FORMAT( CONVERT_TZ( l.start_date,'+00:00', t.`offset`) , '%m/%d/%Y %h:%i %p') AS start_date, DATE_FORMAT(CONVERT_TZ( l.end_date,'+00:00', t.`offset`), '%m/%d/%Y %h:%i %p') AS end_date,
CONCAT_WS('', IF( FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / (60*24))) < 0 , 0, FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time}) / (60*24)))), 'd ', IF(MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / 60)), 24) < 0, 0, MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / 60)), 24)), 'h ', IF( MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time})), 60) < 0, 0, MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time})), 60)), 'm' ) AS duration, IFNULL( FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time}))) , 0) AS open_period,
CASE WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL -1 DAY ) THEN TIMESTAMPDIFF( DAY, CONVERT_TZ( l.start_date, '+00:00', t.`offset` ), DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i') ) WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL -1 HOUR ) THEN TIMESTAMPDIFF( HOUR, CONVERT_TZ( l.start_date, '+00:00', t.`offset` ), DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i') ) WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL -1 MINUTE ) THEN TIMESTAMPDIFF( MINUTE, CONVERT_TZ( l.start_date, '+00:00', t.`offset` ), DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i') ) ELSE 0 END AS times_ago,
CASE WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 DAY ) THEN 'day' WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 HOUR ) THEN 'hour' WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 MINUTE ) THEN 'minute' ELSE 'now' END AS times_ago_unit, ,
FROM `alert` l LEFT JOIN error e ON e.id = l.id_error LEFT JOIN error_level el ON el.id = e.id_error_level LEFT JOIN icon i ON i.id = el.id_icon LEFT JOIN device d ON d.id = l.id_device LEFT JOIN device_group dg ON dg.id = d.id_device_group LEFT JOIN site s ON s.id = d.id_site LEFT JOIN site_employee_map scm ON s.id = scm.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone LEFT JOIN device_type dt ON dt.id = d.id_device_type WHERE e.`status` = 1 AND e.is_delete = 0 AND l.is_delete = 0 AND s.status = 1 AND s.is_delete = 0 AND scm.id_employee = #{id_customer} AND scm.is_hiding = 0
AND l.status = 1
AND l.status = 0
AND CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) BETWEEN #{date_from} AND #{date_to}
AND s.id IN (
#{item.id}
)
AND el.id IN (
#{item.id}
)
AND s.`name` LIKE CONCAT("%",#{site_name}, "%")
AND el.id != 14
GROUP BY l.id
ORDER BY l.`id` ${order_by}
ORDER BY s.`name` ${order_by}
ORDER BY el.`name` ${order_by}
ORDER BY e.`message` ${order_by}
ORDER BY d.`devicename` ${order_by}
ORDER BY l.start_date ${order_by}
ORDER BY l.`start_date` ${order_by}
ORDER BY l.`end_date` ${order_by}
ORDER BY `open_period` ${order_by}
ORDER BY dt.name ${order_by}
ORDER BY unit, d.field_value1 ${order_by}
ORDER BY id DESC
LIMIT #{limit} OFFSET #{offset};

SELECT COUNT(l.id) as totalRecord
FROM `alert` l LEFT JOIN error e ON e.id = l.id_error LEFT JOIN error_level el ON el.id = e.id_error_level LEFT JOIN device d ON d.id = l.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN site_employee_map scm ON s.id = scm.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE e.`status` = 1 AND e.is_delete = 0 AND scm.id_employee = #{id_customer} AND s.status = 1 AND s.is_delete = 0
AND l.status = 1
AND l.status = 0
AND CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) BETWEEN #{date_from} AND #{date_to}
AND s.id IN (
#{item.id}
)
AND el.id IN (
#{item.id}
)
AND s.`name` LIKE CONCAT("%",#{site_name}, "%")
AND el.id != 14

SELECT COUNT( DISTINCT a.id ) AS totalRecord FROM site s LEFT JOIN site_employee_map scm ON s.id = scm.id_site LEFT JOIN device d ON d.id_site = s.id LEFT JOIN alert a ON a.id_device = d.id LEFT JOIN error e ON e.id = a.id_error WHERE
s.`status` = 1 AND s.is_delete = 0 AND a.`status` = 1 AND a.is_delete = 0 AND d.`status` = 1 AND d.is_delete = 0 AND scm.id_employee = #{id_customer} AND scm.is_hiding = 0
AND s.id IN (
#{item.id}
)
AND e.id_error_level != 14

SELECT t.time_full, t.categories_time, CAST(SUM(t.nvmActivePower) AS DECIMAL) AS nvmActivePower FROM (
SELECT FROM_UNIXTIME(((UNIX_TIMESTAMP(CONVERT_TZ(dv.time, 'UTC', #{item.timezone_value})) DIV 900)*900), '%Y-%m-%d %H:%i') AS time_full, FROM_UNIXTIME(((UNIX_TIMESTAMP(CONVERT_TZ(dv.time, 'UTC', #{item.timezone_value})) DIV 900)*900), '%d. %b %H:%i') AS categories_time, AVG(dv.nvmActivePower) AS nvmActivePower FROM ${item.datatablename} dv WHERE dv.time BETWEEN CONVERT_TZ(#{start_date}, #{item.timezone_value}, 'UTC') AND CONVERT_TZ(#{end_date}, #{item.timezone_value}, 'UTC') GROUP BY time_full
) t GROUP BY t.time_full
