mapper.select:
SELECT s.`name` AS site_name, s.id AS id_site, SHA1(s.id) AS hash_id, s.dc_capacity, meter.power_today_meter, inv.power_today_inverter, ROUND(IF(meter.power_today_meter IS NOT NULL || meter.power_today_meter >=0 ,meter.power_today_meter,inv.power_today_inverter), 1) AS power_today, ep.expected, h.is_hiding, ROUND((IF(meter.power_today_meter IS NOT NULL || meter.power_today_meter >=0 ,meter.power_today_meter,inv.power_today_inverter) / ep.expected)*100, 1)AS a_e
FROM `site` s LEFT JOIN ( SELECT SUM(d.last_value) AS power_today_meter, d.id_site FROM device d LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE d.id_device_type IN (3,7,9) AND d.is_excluded_meter = 0 AND d.`status` = 1 AND d.`is_delete` = 0 AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL) GROUP BY d.id_site )meter ON meter.id_site = s.id
LEFT JOIN ( SELECT SUM(d.last_value) AS power_today_inverter, d.id_site FROM device d LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE d.id_device_type = 1 AND d.`status` = 1 AND d.`is_delete` = 0 AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL) GROUP BY d.id_site )inv ON inv.id_site = s.id
LEFT JOIN ( SELECT ee.id_site, ee.`year`, CASE WHEN DATE_FORMAT('2022-08-10', '%b') = 'Jan' THEN ROUND((((ee.jan * 1000)/31) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Feb' THEN ROUND((((ee.feb * 1000)/28) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Mar' THEN ROUND((((ee.mar * 1000)/31) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Apr' THEN ROUND((((ee.apr * 1000)/30) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'May' THEN ROUND((((ee.may * 1000)/31) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Jun' THEN ROUND((((ee.jun * 1000)/30) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Jul' THEN ROUND((((ee.jul * 1000)/31) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Aug' THEN ROUND((((ee.aug * 1000)/31) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Sep' THEN ROUND((((ee.sep * 1000)/30) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Oct' THEN ROUND((((ee.oct * 1000)/31) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Nov' THEN ROUND((((ee.nov * 1000)/30) / (8*0.75))/1000 ,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Dec' THEN ROUND((((ee.`dec` * 1000)/31) / (8*0.75))/1000,1) ELSE 0 END AS expected
FROM energy_expectations ee LEFT JOIN site s ON s.id = ee.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE ee.`year` = DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d') AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL) )ep ON ep.id_site = s.id
LEFT JOIN ( SELECT id_site, MAX(is_hiding) AS is_hiding FROM site_employee_map GROUP BY id_site ) h ON h.id_site = s.id
WHERE s.`status` = 1 AND s.is_delete = 0
AND s.id IN (
#{item.id}
)
GROUP BY s.id
ORDER BY
s.name ${order_by}
power_today ${order_by}
ep.expected ${order_by}
a_e ${order_by}
s.id DESC

SELECT ( SELECT COUNT( e.id_error_level ) AS lowPriority FROM alert a LEFT JOIN device d ON d.id = a.id_device LEFT JOIN error e ON a.id_error = e.id LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE e.id_error_level = 13 AND a.`status` = 1 AND e.`status` = 1 AND a.`is_delete` = 0 AND e.`is_delete` = 0 AND d.`status` = 1 AND d.`is_delete` = 0 AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL)
AND d.id_site IN (
#{item.id}
)
GROUP BY e.id_error_level ) AS lowPriority,
( SELECT COUNT( e.id_error_level ) AS highPriority FROM alert a LEFT JOIN error e ON a.id_error = e.id LEFT JOIN device d ON d.id = a.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE e.id_error_level = 11 AND a.`status` = 1 AND e.`status` = 1 AND a.`is_delete` = 0 AND e.`is_delete` = 0 AND d.`status` = 1 AND d.`is_delete` = 0 AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL)
AND d.id_site IN (
#{item.id}
)
GROUP BY e.id_error_level ) AS highPriority,
( SELECT COUNT(*) AS totalError FROM alert a LEFT JOIN error e ON a.id_error = e.id LEFT JOIN device d ON d.id = a.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE a.`status` = 1 AND e.`status` = 1 AND a.`is_delete` = 0 AND e.`is_delete` = 0 AND d.`status` = 1 AND d.`is_delete` = 0 AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL)
AND d.id_site IN (
#{item.id}
)
) AS totalError,
( SELECT IFNULL(ROUND(SUM(d.last_value), 2), 0) AS generationNow FROM site s LEFT JOIN device d ON d.id_site = s.id LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE d.`status` = 1 AND s.`status` = 1 AND d.id_device_type = 1 AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL)
AND s.id IN (
#{item.id}
)
) AS generationNow,
( SELECT COUNT(s.id) AS totalSite FROM site s WHERE s.`status` = 1
AND s.id IN (
#{item.id}
)
) AS totalSite,
( SELECT SUM(s.dc_capacity) AS ratedDCCapacity FROM site s WHERE s.`status` = 1
AND s.id IN (
#{item.id}
)
) AS ratedDCCapacity
