mapper.select:
SELECT l.id, l.asset, el.name AS priority_name, l.capacity, l.`status`, l.alert_acknowledged, l.disable_notification, l.resolved, IF(l.`status` = 1, 'Opened', 'Closed') AS status_name, DATE_FORMAT(CONVERT_TZ( d.last_updated,'UTC', t.value), '%c/%e/%Y %h:%i %p') AS last_updated, el.`name` AS `level`, e.message, e.error_code, d.devicename, d.datatablename, d.view_tablename, d.job_tablename, AS site_name, s.id AS id_site, SHA1(s.id) AS hash_id, d.id as id_device, h.is_hiding, i.icon AS icon_alert, el.id AS id_error_level, DATE_FORMAT( CONVERT_TZ( l.start_date, t.`offset`, #{offset_timezone} ), #{format_sql_long} ) AS start, DATE_FORMAT( CONVERT_TZ( l.start_date,'+00:00', t.`offset`) , '%m/%d/%Y %H:%i %p') AS start_date, DATE_FORMAT(CONVERT_TZ( l.end_date,'+00:00', t.`offset`), '%m/%d/%Y %H:%i %p') AS end_date,
CASE WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL -1 DAY ) THEN TIMESTAMPDIFF( DAY, CONVERT_TZ( l.start_date, '+00:00', t.`offset` ), DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i') ) WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL -1 HOUR ) THEN TIMESTAMPDIFF( HOUR, CONVERT_TZ( l.start_date, '+00:00', t.`offset` ), DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i') ) WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL -1 MINUTE ) THEN TIMESTAMPDIFF( MINUTE, CONVERT_TZ( l.start_date, '+00:00', t.`offset` ), DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i') ) ELSE 0 END AS times_ago,
CASE WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 DAY ) THEN 'day' WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 HOUR ) THEN 'hour' WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 MINUTE ) THEN 'minute' ELSE 'now' END AS times_ago_unit,
FROM `alert` l LEFT JOIN error e ON e.id = l.id_error LEFT JOIN error_level el ON el.id = e.id_error_level LEFT JOIN icon i ON i.id = el.id_icon LEFT JOIN device d ON d.id = l.id_device LEFT JOIN device_group dg ON dg.id = d.id_device_group LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone LEFT JOIN ( SELECT id_site, MAX(is_hiding) AS is_hiding FROM site_employee_map GROUP BY id_site ) h ON h.id_site = s.id WHERE e.`status` = 1 AND e.is_delete = 0 AND l.is_delete = 0 AND l.status = 1 AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL)
AND s.id IN (
#{item.id}
)
AND el.id != 14
GROUP BY l.id
ORDER BY
l.id ${order_by}
e.error_code ${order_by}
s.`name` ${order_by}
d.`devicename` ${order_by}
key_indicator ${order_by}
l.start_date ${order_by}
e.message ${order_by}
l.start_date DESC

SELECT s.id AS id_site, AS site_name, SHA1(s.id) AS hash_id, s.dc_capacity, ROUND(IFNULL(vir.activePower, IFNULL(meter.activePower, inv.activePower)), 1) AS power_today, ROUND(IFNULL(vir.expected_power_virtual, IFNULL(wea.expected_power, ep.expected)), 1) AS expected, ROUND(IFNULL(vir.activePower, IFNULL(meter.activePower, inv.activePower)) / IFNULL(vir.expected_power_virtual, IFNULL(wea.expected_power, ep.expected)) * 100, 1) AS a_e, h.is_hiding
FROM `site` s LEFT JOIN time_zone t ON t.id = s.id_time_zone LEFT JOIN () vir ON vir.id_site = s.id LEFT JOIN () inv ON inv.id_site = s.id LEFT JOIN () meter ON meter.id_site = s.id LEFT JOIN () wea ON wea.id_site = s.id
LEFT JOIN ( SELECT ee.id_site, ee.`year`, CASE WHEN DATE_FORMAT('2022-08-10', '%b') = 'Jan' THEN ROUND((((ee.jan * 1000)/31) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Feb' THEN ROUND((((ee.feb * 1000)/28) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Mar' THEN ROUND((((ee.mar * 1000)/31) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Apr' THEN ROUND((((ee.apr * 1000)/30) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'May' THEN ROUND((((ee.may * 1000)/31) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Jun' THEN ROUND((((ee.jun * 1000)/30) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Jul' THEN ROUND((((ee.jul * 1000)/31) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Aug' THEN ROUND((((ee.aug * 1000)/31) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Sep' THEN ROUND((((ee.sep * 1000)/30) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Oct' THEN ROUND((((ee.oct * 1000)/31) / (8*0.75))/1000,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Nov' THEN ROUND((((ee.nov * 1000)/30) / (8*0.75))/1000 ,1) WHEN DATE_FORMAT('2022-08-10', '%b') = 'Dec' THEN ROUND((((ee.`dec` * 1000)/31) / (8*0.75))/1000,1) ELSE 0 END AS expected
FROM energy_expectations ee LEFT JOIN site s ON s.id = ee.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE ee.`year` = DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d') AND (CONVERT_TZ(NOW(), '+00:00', t.offset) < s.expiration OR s.expiration IS NULL) )ep ON ep.id_site = s.id
LEFT JOIN ( SELECT id_site, MAX(is_hiding) AS is_hiding FROM site_employee_map GROUP BY id_site ) h ON h.id_site = s.id
WHERE s.`status` = 1 AND s.is_delete = 0 AND (CONVERT_TZ(UTC_TIMESTAMP(), 'UTC', t.value) < s.expiration OR s.expiration IS NULL)
AND s.id IN (
#{item.id}
)
GROUP BY s.id
ORDER BY
s.name ${order_by}
power_today ${order_by}
expected ${order_by}
a_e ${order_by}
s.id DESC

