( SELECT COUNT(s.id) AS totalSite FROM site s WHERE s.`status` = 1
AND s.id IN (
#{item.id}
)
) AS totalSite,
( SELECT SUM(s.dc_capacity) AS ratedDCCapacity FROM site s WHERE s.`status` = 1
AND s.id IN (
#{item.id}
)
) AS ratedDCCapacity
