mapper.insert:
	INSERT INTO `device_layout_template`(
		`name`,
		`data`
	)VALUES(
		#{name},
		#{data}
	);

        SELECT 
        LAST_INSERT_ID() as id

mapper.delete:
DELETE FROM device_template_field_map
WHERE
	id_device_group = #{id_device_group}
	AND id_categorize_data = #{id_categorize_data}
