mapper.select:
SELECT id, CONCAT_WS( ' ', first_name, last_name ) AS fullname, CONCAT_WS( ' ', first_name, last_name ) AS text, CONCAT_WS( ' ', first_name, last_name ) AS label, id AS value, email, GROUP_CONCAT(er.id_role) AS role_ids FROM `employee` e LEFT JOIN employee_role_map er ON er.id_employee = e.id WHERE `status` = 1 AND is_delete = 0
AND e.id_company = #{id_company}
AND e.email = #{email}
GROUP BY e.id ORDER BY id DESC;

mapper.sql:
SELECT er.id_employee, GROUP_CONCAT(r.`name` SEPARATOR ', ') AS group_roles, GROUP_CONCAT(r.`id`) AS role_ids, r.color, IF( COUNT(r.id) = 0, JSON_ARRAY(), JSON_ARRAYAGG(JSON_OBJECT( 'id', r.id, 'name', r.name, 'color', r.color )) ) AS data_roles FROM employee_role_map er LEFT JOIN employee e ON e.id = er.id_employee LEFT JOIN role r ON r.id = er.id_role WHERE r.status = 1 AND r.is_delete = 0
AND r.id IN (
#{item.id}
)
AND e.id_company = #{id_company}
AND r.id = #{id_role}
GROUP BY er.id_employee

mapper.select:
SELECT e.id, e.first_name, e.last_name, e.phone, e.email, e.email AS re_email, e.address, e.is_delete, e.`status`, e.skype, e.avatar, DATE_FORMAT(e.birthday, '%m/%d%Y') AS birthday, CONCAT_WS(' ',e.first_name,e.last_name) AS fullname, t.group_roles, t.role_ids, t.color, t.data_roles, m.manage_site_total, m.ids_site, m.site_name AS site_names, CONVERT_TZ(s.last_login, s.offset_timezone, 'UTC') AS last_login, s.page_login, e.account_locked, e.failed_attempt, e.lock_time, e.created_by, SHA2(e.`id`, 256) AS hash_id_user, e.id_company, e.note, e.portfolio_metrics_enable, e.is_admin_company FROM `employee` e LEFT JOIN () m ON m.id_employee = e.id LEFT JOIN ()t ON t.id_employee = e.id LEFT JOIN ( SELECT * FROM account_status WHERE (id_employee, id) IN ( SELECT id_employee, MAX(id) AS id FROM account_status GROUP BY id_employee ) )s ON s.id_employee = e.id
WHERE
GROUP BY e.id ORDER BY
CONCAT(e.first_name, ' ', e.last_name) ${order_by}
t.group_roles ${order_by}
last_login ${order_by}
m.manage_site_total ${order_by}
e.account_locked ${order_by}
e.${sort_column} ${order_by}
t.group_roles ASC
, e.id DESC
LIMIT ${limit} OFFSET ${offset};
