SELECT s.id, s.`message`, s.`error_code`, s.`status`, s.is_delete, s.`id_error_level`, s.`id_device_group`, t.group_name AS error_type_name, t.code_prefix, l.name AS priority_name, l.`name` AS error_level_name, s.id_error_type, s.description, s.nw, s.client, (SELECT COUNT(m.id_role) as nw_interal FROM (SELECT em.id_role, e.id, e.email FROM employee e LEFT JOIN employee_role_map em ON em.id_employee = e.id WHERE e.id = #{id_employee} ) m WHERE (IF ( 1 IN (m.id_role) OR 12 IN (m.id_role) OR 15 IN (m.id_role), 1 , 0))) AS nw_internal
FROM error s LEFT JOIN error_level l ON l.id = s.id_error_level LEFT JOIN device_group t ON t.id = s.id_device_group WHERE s.is_delete = 0
AND IF ((SELECT COUNT(m.id_role) as nw_interal FROM (SELECT em.id_role, e.id, e.email FROM employee e LEFT JOIN employee_role_map em ON em.id_employee = e.id WHERE e.id = #{id_employee} ) m WHERE (IF ( 1 IN (m.id_role) OR 12 IN (m.id_role) OR 15 IN (m.id_role), 1 , 0))) > 0, s.nw = 1, s.client = 1)
AND l.id IN (
#{item.id}
)
AND s.id_device_group IN (
#{item.id}
)
AND s.message IN (
#{item.message}
)
AND s.message LIKE CONCAT("%",#{keyword}, "%")
order by
s.id ${order_by}
`status` ${order_by}
s.id DESC
LIMIT ${limit} OFFSET ${offset};

