mapper.select:
SELECT f.id, f.id_device, f.id_employee, f.filename, f.`status`, IF( f.time_complete IS NULL, "", DATE_FORMAT(f.time_complete, "%d/%m/%Y %H:%i:%s")) AS time_complete, IF( f.time_upload IS NULL, "", DATE_FORMAT(f.time_upload, "%d/%m/%Y %H:%i:%s")) AS time_upload, f.total_complete_row, f.total_error_row, f.total_row, d.devicename, s.`name` AS site_name, t.`value` AS timezone_value, CONCAT_WS( " ", e.first_name, e.last_name ) AS fullname, CASE WHEN f.`status` = 1 THEN "Uploaded" WHEN f.`status` = 2 THEN "In Process" WHEN f.`status` = 3 THEN "Completed" WHEN f.`status` = 4 THEN "Error" ELSE "" END AS status_name, IF ( f.total_complete_row <= 0 OR f.total_row <= 0, 0, ROUND( ( f.total_complete_row / f.total_row ) * 100, 4 )) upload_process FROM file_import_data_old f LEFT JOIN employee e ON e.id = f.id_employee LEFT JOIN device d ON d.id = f.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE s.`status` = 1 AND s.is_delete = 0 AND d.`status` = 1 AND d.is_delete = 0 AND e.`status` = 1
AND f.filename LIKE CONCAT("%",#{keyword}, "%")
order by
f.id ${order_by}
f.`status` ${order_by}
f.id DESC
LIMIT ${limit} OFFSET ${offset};
