mapper.select:
SELECT s.id, SHA1(s.id) AS hash_id, s.id_country, s.id_time_zone, s.`name`, s.street, s.lat, s.lng, built_since, s.old_data, s.number, s.postal_code, s.city, s.state, commissioning, s.emergency_contact, s.ac_capacity, s.dc_capacity, s.`status`, s.is_delete, s.created_date, s.created_by, s.updated_date, s.updated_by, s.gallery, s.weather_icon, s.weather_description, sg.id AS site_group_id, sg.name AS site_group_name, sg.order_id AS site_group_order, IFNULL(t.total_error, 0) AS total_error, CONCAT_WS(', ',s.city, s.state) AS address, IF(m.inverter_power IS NULL, m.inverter_power, ROUND(m.inverter_power, 2)) AS inverter_power, IF(n.meter_power IS NULL, n.meter_power, ROUND(n.meter_power, 2)) AS meter_power, IFNULL(low.low_error, 0) AS low_error, IFNULL(med.medium_error, 0) AS medium_error, IFNULL(heigh.heigh_error, 0) AS heigh_error
FROM site AS s LEFT JOIN site_employee_map sem ON sem.id_site = s.id LEFT JOIN site_group sg ON sg.id = s.id_site_group LEFT JOIN ( SELECT SUM(d.last_value) AS inverter_power, s.id FROM site s LEFT JOIN device d ON d.id_site = s.id WHERE d.id_device_type = 1 AND s.`status` = 1 AND d.`status` = 1 GROUP BY s.id )m ON m.id = s.id
LEFT JOIN ( SELECT SUM(d.last_value) AS meter_power, s.id FROM site s LEFT JOIN device d ON d.id_site = s.id WHERE d.id_device_type IN (3,7,9) AND d.is_excluded_meter = 0 AND s.`status` = 1 AND d.`status` = 1
AND s.id IN (
#{item.id}
)
GROUP BY s.id )n ON n.id = s.id
LEFT JOIN ( SELECT COUNT( a.id ) AS total_error, s.id FROM site s LEFT JOIN device d ON d.id_site = s.id LEFT JOIN alert a ON a.id_device = d.id WHERE a.`status` = 1 AND d.`status` = 1
AND s.id IN (
#{item.id}
)
GROUP BY s.id )t ON s.id = t.id
LEFT JOIN ( SELECT COUNT( a.id ) AS low_error, s.id FROM site s LEFT JOIN device d ON d.id_site = s.id LEFT JOIN alert a ON a.id_device = d.id LEFT JOIN error e ON e.id = a.id_error LEFT JOIN error_level el ON el.id = e.id_error_level WHERE a.`status` = 1 AND el.`level` = 1 AND d.`status` = 1
AND s.id IN (
#{item.id}
)
GROUP BY s.id )low ON s.id = low.id
LEFT JOIN ( SELECT COUNT( a.id ) AS medium_error, s.id FROM site s LEFT JOIN device d ON d.id_site = s.id LEFT JOIN alert a ON a.id_device = d.id LEFT JOIN error e ON e.id = a.id_error LEFT JOIN error_level el ON el.id = e.id_error_level WHERE a.`status` = 1 AND el.`level` = 2 AND d.`status` = 1
AND s.id IN (
#{item.id}
)
GROUP BY s.id )med ON s.id = med.id
LEFT JOIN ( SELECT COUNT( a.id ) AS heigh_error, s.id FROM site s LEFT JOIN device d ON d.id_site = s.id LEFT JOIN alert a ON a.id_device = d.id LEFT JOIN error e ON e.id = a.id_error LEFT JOIN error_level el ON el.id = e.id_error_level WHERE a.`status` = 1 AND el.`level` = 3 AND d.`status` = 1
AND s.id IN (
#{item.id}
)
GROUP BY s.id )heigh ON s.id = heigh.id
WHERE s.is_delete = 0 AND s.`status` = 1
AND s.id IN (
#{item.id}
)
AND s.name LIKE CONCAT("%",#{name}, "%")
GROUP BY s.id
