SELECT s.id, SHA1(s.id) AS hash_id, IFNULL(er.total_error, 0) AS total_error, IFNULL(er.alerts, JSON_ARRAY()) AS alerts, AS `name`, DATE_FORMAT(s.expiration, '%m/%d/%Y') AS expiration, s.kiosk_view, IFNULL(s.ac_capacity, 0) as ac_capacity, IFNULL(s.dc_capacity, 0) as dc_capacity, DATE_FORMAT(s.built_since,'%m/%d/%Y') AS built_since,
CONCAT_WS(""," #", s.id)
c.name
AS company_name,
s.weather_icon, s.weather_description, s.is_subtract_pv, s.temperature_2m_min, s.temperature_2m_max, s.id_company, s.is_rec_report, p.last_updated, DATE_FORMAT(s.expiration, '%m/%d/%Y') AS expiration, p.times_ago_unit, IFNULL(p.unix_time, 0) AS unix_time, s.note, h.is_hiding, tag.tags, IFNULL(sgm.id_site_group, 0 ) AS id_site_group, IFNULL(s.id_site_group, 0 ) AS id_site_group_old, IF(CONVERT_TZ(UTC_TIMESTAMP(), 'UTC', tz.value) < s.expiration OR s.expiration IS NULL, p.times_ago, NULL) AS times_ago, IF(CONVERT_TZ(UTC_TIMESTAMP(), 'UTC', tz.value) < s.expiration OR s.expiration IS NULL, IFNULL(ROUND(t.irradiance, 2), IF(t.id_device_type = 4, 0, NULL)), NULL) AS irradiance, IF(CONVERT_TZ(UTC_TIMESTAMP(), 'UTC', tz.value) < s.expiration OR s.expiration IS NULL, IFNULL(s.eer_last_month, 0), NULL) AS EERLastMonth, IF(CONVERT_TZ(UTC_TIMESTAMP(), 'UTC', tz.value) < s.expiration OR s.expiration IS NULL, IFNULL(s.eer_this_month, 0), NULL) AS EERThisMonth, IF(CONVERT_TZ(UTC_TIMESTAMP(), 'UTC', tz.value) < s.expiration OR s.expiration IS NULL, ROUND(s.PerformanceRatioYesterday, 1), NULL) AS PerformanceRatioYesterday, IF(CONVERT_TZ(UTC_TIMESTAMP(), 'UTC', tz.value) < s.expiration OR s.expiration IS NULL, ROUND(IFNULL(v.activePower, IFNULL(m.activePower, ivt.activePower)), 1), NULL) AS activePower, IF(CONVERT_TZ(UTC_TIMESTAMP(), 'UTC', tz.value) < s.expiration OR s.expiration IS NULL, ROUND(IFNULL(v.expected_power_virtual, t.expected_power), 1), NULL) AS expected_power, IF(CONVERT_TZ(UTC_TIMESTAMP(), 'UTC', tz.value) < s.expiration OR s.expiration IS NULL, im.devices_list, NULL) AS devices_list
FROM site s LEFT JOIN company c ON c.id = s.id_company LEFT JOIN site_employee_map sem ON sem.id_site = s.id LEFT JOIN time_zone tz ON tz.id = s.id_time_zone LEFT JOIN site_group_map sgm ON sgm.id_site = s.id
LEFT JOIN ( SELECT id_site, MAX(is_hiding) AS is_hiding FROM site_employee_map GROUP BY id_site ) h ON h.id_site = s.id
LEFT JOIN () v ON v.id_site = s.id LEFT JOIN () t ON t.id_site = s.id LEFT JOIN () ivt ON ivt.id_site = s.id LEFT JOIN () m ON m.id_site = s.id
LEFT JOIN ( SELECT t.id_site, SUM(t.total_error) AS total_error, JSON_ARRAYAGG(obj_alert) AS alerts FROM () t GROUP BY t.id_site ) er ON er.id_site = s.id
LEFT JOIN () im ON im.id_site = s.id
LEFT JOIN ( SELECT d.id_site, DATE_FORMAT(CONVERT_TZ( d.last_updated, 'UTC', t.value ), '%m/%d/%Y %h:%i:%s %p' ) AS last_updated, UNIX_TIMESTAMP(d.last_updated) AS unix_time,
CASE WHEN CONVERT_TZ( d.last_updated, 'UTC', t.value ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( UTC_TIMESTAMP(), 'UTC', t.value ) , '%Y-%m-%d %H:%i'), INTERVAL -1 DAY ) THEN TIMESTAMPDIFF( DAY, CONVERT_TZ( d.last_updated, 'UTC', t.value ), DATE_FORMAT( CONVERT_TZ( UTC_TIMESTAMP(), 'UTC', t.value ) , '%Y-%m-%d %H:%i') ) WHEN CONVERT_TZ( d.last_updated, 'UTC', t.value ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( UTC_TIMESTAMP(), 'UTC', t.value ) , '%Y-%m-%d %H:%i'), INTERVAL -1 HOUR ) THEN TIMESTAMPDIFF( HOUR, CONVERT_TZ( d.last_updated, 'UTC', t.value ), DATE_FORMAT( CONVERT_TZ( UTC_TIMESTAMP(), 'UTC', t.value ) , '%Y-%m-%d %H:%i') ) WHEN CONVERT_TZ( d.last_updated, 'UTC', t.value ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( UTC_TIMESTAMP(), 'UTC', t.value ) , '%Y-%m-%d %H:%i'), INTERVAL -1 MINUTE ) THEN TIMESTAMPDIFF( MINUTE, CONVERT_TZ( d.last_updated, 'UTC', t.value ), DATE_FORMAT( CONVERT_TZ( UTC_TIMESTAMP(), 'UTC', t.value ) , '%Y-%m-%d %H:%i') ) ELSE 0 END AS times_ago,
CASE WHEN CONVERT_TZ( d.last_updated, 'UTC', t.value ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( UTC_TIMESTAMP(), 'UTC', t.value ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 DAY ) THEN 'day' WHEN CONVERT_TZ( d.last_updated, 'UTC', t.value ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( UTC_TIMESTAMP(), 'UTC', t.value ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 HOUR ) THEN 'hour' WHEN CONVERT_TZ( d.last_updated, 'UTC', t.value ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( UTC_TIMESTAMP(), 'UTC', t.value ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 MINUTE ) THEN 'minute' ELSE 'now' END AS times_ago_unit
FROM device d LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone LEFT JOIN site_employee_map sem ON sem.id_site = s.id WHERE d.id_device_group = 19
AND sem.id_employee = #{id_employee}
AND s.`status` = 1 AND s.is_delete = 0 AND d.`status` = 1 AND d.is_delete = 0 GROUP BY s.id ORDER BY unix_time ASC )p ON p.id_site = s.id
LEFT JOIN ( SELECT t.id_site, JSON_ARRAYAGG(obj_tag) AS tags FROM ( SELECT s.id AS id_site, JSON_OBJECT('id', t.id, 'name', t.`name`) AS obj_tag FROM tag_site_map tsm LEFT JOIN site s ON s.id = tsm.id_site LEFT JOIN tag t On t.id = tsm.id_tag WHERE s.`status` = 1 AND s.`is_delete` = 0 ) t GROUP BY t.id_site ) tag ON tag.id_site = s.id
WHERE s.status = 1 and s.is_delete = 0
AND sem.id_employee = #{id_employee} AND sem.is_hiding = 0
AND SHA2(s.id_site_group, 256) = #{hash_id_site_group}
AND (s.id_site_group = #{id_site_group} OR sgm.id_site_group = #{id_site_group})
AND SHA2(s.id_site_sub_group, 512) = #{hash_id_site_sub_group}
AND s.name LIKE CONCAT("%",#{value_filter}, "%")
AND s.id = #{value_filter}
AND s.dc_capacity = #{value_filter}
AND s.built_since = #{value_filter}
AND c.name LIKE CONCAT("%",#{value_filter}, "%")
AND s.name LIKE CONCAT("%",#{name}, "%")
AND s.id_company = #{id_company}
AND s.id IN (
#{item.id}
)
GROUP BY s.id
order by
s.is_rec_report ${order_by}
s.name ${order_by}
s.dc_capacity ${order_by}
EERThisMonth ${order_by}
EERLastMonth ${order_by}
s.PerformanceRatioYesterday ${order_by}
ROUND(IFNULL(m.activePower, ivt.activePower), 1) / ROUND(IFNULL(v.expected_power_virtual, t.expected_power), 1) ${order_by}
total_error ${order_by}
s.kiosk_view ${order_by}
s.id_company ${order_by}
s.id ${order_by}
activePower ${order_by}
s.weather_icon ${order_by}
irradiance ${order_by}
s.built_since ${order_by}
p.unix_time ${order_by}
s.expiration ${order_by}
s.`name`, s.id_company ASC

