SELECT s.id AS site_id, c.highAvailability, c.moderateAvailabilityBetween, c.moderateAvailabilityAnd, c.lowAvailability, c.highPerformance, c.moderatePerformanceBetween, c.moderatePerformanceAnd, c.lowPerformance, AS site_name, ROUND((a.total_inverters - IFNULL(b.unavailable_inverters, 0)) / a.total_inverters, 3) AS availability, im.devices_list AS devices_list FROM site s LEFT JOIN company c ON c.id = s.id_company JOIN time_zone t ON t.id = s.id_time_zone LEFT JOIN ( SELECT d.id_site, COUNT(*) AS total_inverters FROM device d WHERE d.`status` = 1 AND d.is_delete = 0 AND d.id_device_type = 1
GROUP BY d.id_site ) AS a ON a.id_site = s.id LEFT JOIN ( SELECT d.id_site, COUNT(*) AS unavailable_inverters FROM ( SELECT d.id_site, d.id FROM device d JOIN alert a ON a.id_device = d.id JOIN error e ON e.id = a.id_error WHERE d.`status` = 1 AND d.is_delete = 0 AND d.id_device_type = 1 AND a.`status` = 1 AND a.resolved = 0 AND (e.id_error_level = 32 OR e.id_error_level = 33)
GROUP BY d.id ) d GROUP BY d.id_site ) AS b ON b.id_site = s.id LEFT JOIN () im ON im.id_site = s.id WHERE
#{item}
AND SHA2(s.id_site_group, 256) = #{hash_id_site_group}
AND SHA2(s.id_site_sub_group, 512) = #{hash_id_site_sub_group}

