mapper.select:
SELECT s.id, s.id AS value, s.`name` AS text, s.`name` AS label FROM site AS s WHERE s.is_delete = 0 AND s.`status` = 1
AND s.id IN (
#{item.id}
)
ORDER BY s.`name` ASC

SELECT ssg.id, ssg.id_site_group, ssg.`name` AS text FROM site s LEFT JOIN site_sub_group ssg ON ssg.id = s.id_site_sub_group LEFT JOIN site_group sg ON sg.id = ssg.id_site_group WHERE ssg.status = 1 AND ssg.is_delete = 0 AND sg.status = 1 AND sg.is_delete = 0
AND s.id IN (
#{item.id}
)
GROUP BY ssg.id ORDER BY ssg.id ASC

SELECT CASE WHEN r.type_report = 1 THEN CASE WHEN r.type_option = 1 THEN CONCAT_WS(" " , t1.sites_name, "Solar Production Report") WHEN r.type_option = 2 THEN CONCAT_WS(" " , t2.sites_name, "Solar Production Report") ELSE CONCAT_WS(" " , s.`name`, "Solar Production Report") END WHEN r.type_report = 2 THEN CASE WHEN r.type_option = 1 THEN CONCAT_WS(" " , t1.sites_name, "Production Trend Report") WHEN r.type_option = 2 THEN CONCAT_WS(" " , t2.sites_name, "Production Trend Report") WHEN r.type_option = 3 THEN CONCAT_WS(" " , t3.sites_name, "Sub-Group", "Production Trend Report") END WHEN r.type_report = 3 THEN CONCAT_WS(" " , s.`name`, "Leviton BMO Consumption Report") WHEN r.type_report = 4 THEN CONCAT_WS(" " , s.`name`, "Asset Management and Operation Performance Report") ELSE s.`name` END AS report_name, CASE WHEN r.cadence_range = 1 THEN "Daily" WHEN r.cadence_range = 2 THEN "Monthly" WHEN r.cadence_range = 3 THEN "Quarterly" WHEN r.cadence_range = 4 THEN "Annual" WHEN r.cadence_range = 5 THEN "Custom" WHEN r.cadence_range = 6 THEN "Weekly" ELSE "" END AS cadence_range_name, r.id, s.id AS id_site, s.table_data_report, r.id_employee, r.cadence_range, r.type_report, r.data_intervals, r.subscribers, r.file_type, r.type_option, r.id_sub_group, DATE_FORMAT(r.date_from, '%Y-%m-%d %H:%i:%s') AS date_from, DATE_FORMAT(r.date_to, '%Y-%m-%d %H:%i:%s') AS date_to, r.`status`, r.schedule_enable, DATE_FORMAT(r.time_schedule, '%Y-%m-%d %H:%i') AS time_schedule, r.periodicity, r.days_week, DATE_FORMAT(r.created_date, '%m/%d/%Y %H:%i %p') AS created_on, DATE_FORMAT(r.updated_date, '%m/%d/%Y %H:%i %p') AS last_modified, CASE WHEN r.type_option = 1 THEN t1.ids_site WHEN r.type_option = 2 THEN t2.ids_site WHEN r.type_option = 3 THEN t3.ids_site ELSE t2.ids_site END AS ids_site, CASE WHEN r.type_option = 1 THEN t1.sitesListJSON WHEN r.type_option = 2 THEN t2.sitesListJSON WHEN r.type_option = 3 THEN t3.sitesListJSON ELSE t2.sitesListJSON END AS sitesListJSON FROM report r LEFT JOIN site s ON s.id = r.id_site LEFT JOIN time_zone tz ON tz.id = s.id_time_zone LEFT JOIN ( SELECT sem.id_employee, "Entire Portfolio" AS sites_name, GROUP_CONCAT(s.id SEPARATOR ',') AS ids_site, JSON_ARRAYAGG(JSON_OBJECT( 'id', s.id, 'text', s.name )) AS sitesListJSON FROM site_employee_map sem LEFT JOIN site s ON s.id = sem.id_site WHERE s.`status` = 1 AND s.is_delete = 0 GROUP BY sem.id_employee ) t1 ON t1.id_employee = r.id_employee LEFT JOIN ( SELECT rsm.id_report, GROUP_CONCAT(s.name SEPARATOR ' - ') AS sites_name, GROUP_CONCAT(s.id SEPARATOR ',') AS ids_site, JSON_ARRAYAGG(JSON_OBJECT( 'id', s.id, 'text', s.name )) AS sitesListJSON FROM report_site_map rsm LEFT JOIN site s ON s.id = rsm.id_site WHERE s.`status` = 1 AND s.is_delete = 0 GROUP BY rsm.id_report ) t2 ON t2.id_report = r.id LEFT JOIN ( SELECT s.id_site_sub_group, ssg.name AS sites_name, GROUP_CONCAT(s.id SEPARATOR ',') AS ids_site, JSON_ARRAYAGG(JSON_OBJECT( 'id', s.id, 'text', s.name )) AS sitesListJSON FROM site s LEFT JOIN site_sub_group ssg ON ssg.id = s.id_site_sub_group WHERE s.`status` = 1 AND s.is_delete = 0 GROUP BY s.id_site_sub_group ) t3 ON t3.id_site_sub_group = r.id_sub_group WHERE r.status = 1 AND (CONVERT_TZ(NOW(), '+00:00', tz.offset) < s.expiration OR s.expiration IS NULL)
AND r.id_employee = #{id_employee}
GROUP BY r.id order by
r.id ${order_by}
r.name ${order_by}
r.id DESC
LIMIT ${limit} OFFSET ${offset};

SELECT s.id, s.name, s.id AS id_site, IF(s.rec_id IS NULL || s.rec_id = '', CONCAT_WS("-", "NWM", s.id), s.rec_id) AS rec_id, IF(s.gu_id IS NULL || s.gu_id = '', CONCAT_WS("-", "NWM", s.id), s.gu_id) AS gu_id, IFNULL(ROUND(SUM( d.energy_this_month ) / 1000, 1), 0 ) AS energy_this_month, DATE_FORMAT( #{start_date}, "%m/%d/%Y") AS start_date, DATE_FORMAT(#{end_date}, "%m/%d/%Y") AS end_date, DATE_FORMAT(#{end_date}, "%m/%Y") AS vintage_date FROM site s LEFT JOIN device d ON d.id_site = s.id WHERE s.`status` = 1 AND d.`status` = 1
AND s.id IN (
#{item.id}
)
GROUP BY s.id
