update.if:
UPDATE site SET
`display_time_format` = #{display_time_format},
`display_date_format` = #{display_date_format},
`id_time_zone` = #{id_time_zone}
WHERE id IN(
		SELECT sem.id_site FROM site_employee_map sem WHERE sem.id_employee = #{id_employee}
	)

mapper.select:
SELECT s.id, s.id_company, s.id_country, s.id_time_zone, s.`name`, s.street, IF(s.lat = 0, NULL, s.lat) AS lat, IF(s.lng = 0, NULL, s.lng) AS lng, IF(s.lat = 0, NULL, s.lat) AS view_lat, IF(s.lng = 0, NULL, s.lng) AS view_lng, built_since, s.old_data, s.number, s.postal_code, s.city, s.state, commissioning, s.emergency_contact, s.ac_capacity, s.dc_capacity, s.status, s.is_delete, s.created_date, s.created_by, s.updated_date, s.updated_by, s.gallery, s.data_send_time, s.start_date_time, s.end_date_time, s.config_sunset_sunrise, s.note, s.is_rec_report, s.datalogger_ip, s.cf_start_time, s.cf_end_time, s.cf_alert_threshold, s.cf_email_subscribers, s.id AS id_site, s.alert_mail_bcc, s.alert_mail_cc
FROM site s LEFT JOIN site_employee_map sem ON sem.id_site = s.id WHERE sem.id_employee = #{id_employee} AND s.`status` = 1 AND s.is_delete = 0
AND s.name LIKE CONCAT("%",#{keyword}, "%")
GROUP BY s.id
order by
s.id ${order_by}
s.name ${order_by}
s.`name` ASC
LIMIT ${limit} OFFSET ${offset};
