mapper.select:
SELECT l.id, l.asset, el.name AS priority_name, l.capacity, l.`status`, l.alert_acknowledged, l.disable_notification, l.resolved, l.note, IFNULL(dg.title_trans, "N/A") AS device_group_title_trans, IFNULL(el.title_trans, "N/A") AS err_level_title_trans, IFNULL(dt.title_trans, "N/A") AS device_type_title_trans, IFNULL(e.title_trans, "N/A") AS error_title_trans, CASE WHEN COUNT(t.id_alert) > 0 THEN CONCAT( '[', GROUP_CONCAT( JSON_OBJECT( 'fullname', t.fullname, 'created_date', t.date_view )), ']' ) ELSE NULL END AS user_history,
IF(l.`status` = 1, 'Opened', 'Closed') AS status_name, el.`name` AS `level`, e.message, e.error_code, e.id AS id_error, e.troubleshoot_enable, d.devicename, AS site_name, s.id AS id_site, d.id as id_device, i.icon AS icon_alert, el.id AS id_error_level, dt.name AS device_type_name, el.color AS color_error_level,
d.id_device_type, d.datatablename, d.view_tablename, d.job_tablename, dg.group_name, dg.manufacturer_phone, dg.manufacturer_email, dg.manufacturer_name, d.id AS id_device, DATE_FORMAT( CONVERT_TZ( l.start_date,'UTC', t.value), '%Y-%m-%d %H:%i:%s' ) AS start, DATE_FORMAT( CONVERT_TZ( l.start_date,'UTC', t.value) , '%m/%d/%Y %h:%i %p') AS start_date, DATE_FORMAT(CONVERT_TZ( l.end_date,'UTC', t.value), '%m/%d/%Y %h:%i %p') AS end_date, DATE_FORMAT(CONVERT_TZ( d.last_updated,'UTC', t.value), '%m/%d/%Y %h:%i %p') AS last_updated,
CONCAT_WS('', IF( FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'UTC', t.value),#{current_time}) / (60*24))) < 0 , 0, FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'UTC', t.value), #{current_time}) / (60*24)))), 'd ', IF(MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'UTC', t.value),#{current_time}) / 60)), 24) < 0, 0, MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'UTC', t.value),#{current_time}) / 60)), 24)), 'h ', IF( MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'UTC', t.value), #{current_time})), 60) < 0, 0, MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'UTC', t.value), #{current_time})), 60)), 'm' ) AS duration, IFNULL( FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'UTC', t.value), #{current_time}))) , 0) AS open_period,
CASE WHEN CONVERT_TZ( l.start_date, 'UTC', t.value ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), 'UTC', t.value ) , '%Y-%m-%d %H:%i'), INTERVAL -1 DAY ) THEN TIMESTAMPDIFF( DAY, CONVERT_TZ( l.start_date, 'UTC', t.value ), DATE_FORMAT( CONVERT_TZ( NOW(), 'UTC', t.value ) , '%Y-%m-%d %H:%i') ) WHEN CONVERT_TZ( l.start_date, 'UTC', t.value ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), 'UTC', t.value ) , '%Y-%m-%d %H:%i'), INTERVAL -1 HOUR ) THEN TIMESTAMPDIFF( HOUR, CONVERT_TZ( l.start_date, 'UTC', t.value ), DATE_FORMAT( CONVERT_TZ( NOW(), 'UTC', t.value ) , '%Y-%m-%d %H:%i') ) WHEN CONVERT_TZ( l.start_date, 'UTC', t.value ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), 'UTC', t.value ) , '%Y-%m-%d %H:%i'), INTERVAL -1 MINUTE ) THEN TIMESTAMPDIFF( MINUTE, CONVERT_TZ( l.start_date, 'UTC', t.value ), DATE_FORMAT( CONVERT_TZ( NOW(), 'UTC', t.value ) , '%Y-%m-%d %H:%i') ) ELSE 0 END AS times_ago,
CASE WHEN CONVERT_TZ( l.start_date, 'UTC', t.value ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), 'UTC', t.value ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 DAY ) THEN 'day' WHEN CONVERT_TZ( l.start_date, 'UTC', t.value ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), 'UTC', t.value ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 HOUR ) THEN 'hour' WHEN CONVERT_TZ( l.start_date, 'UTC', t.value ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), 'UTC', t.value ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 MINUTE ) THEN 'minute' ELSE 'now' END AS times_ago_unit,
FROM `alert` l LEFT JOIN error e ON e.id = l.id_error LEFT JOIN error_level el ON el.id = e.id_error_level LEFT JOIN icon i ON i.id = el.id_icon LEFT JOIN device d ON d.id = l.id_device LEFT JOIN device_group dg ON dg.id = d.id_device_group LEFT JOIN device_type dt ON dt.id = d.id_device_type
LEFT JOIN ( SELECT ah.id_alert, CONCAT_WS( ' ',e.first_name, e.last_name) AS fullname, DATE_FORMAT(ah.created_date, '%Y-%m-%d %H:%i:%s') AS date_view FROM alert_history ah LEFT JOIN employee e ON ah.id_employee = e.id GROUP BY ah.id_alert ) t ON t.id_alert = l.id
LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE e.`status` = 1 AND s.enable_alert = 1 AND e.is_delete = 0 AND d.`status` = 1 AND d.is_delete = 0 AND s.id = #{id_site} AND l.is_delete = 0 AND (CONVERT_TZ(NOW(), 'UTC', t.value) < s.expiration OR s.expiration IS NULL)
AND el.id != 14
GROUP BY l.id
ORDER BY l.`id` ${order_by}
ORDER BY s.`name` ${order_by}
ORDER BY el.`name` ${order_by}
ORDER BY e.`message` ${order_by}
ORDER BY d.`devicename` ${order_by}
ORDER BY l.`start_date` ${order_by}
ORDER BY l.`end_date` ${order_by}
ORDER BY l.start_date ${order_by}
ORDER BY dt.name ${order_by}
ORDER BY l.start_date DESC
LIMIT #{limit} OFFSET #{offset};

SELECT
	* 
FROM
	alert_history s 
WHERE
	s.`id_alert` = #{id_alert} 
	AND s.`id_employee` = #{id_employee} 
	LIMIT 1;

  		SELECT
	  		(


				AND s.id = #{id_site}
			) AS lowPriority,
			(


				AND s.id = #{id_site}
			) AS highPriority,
			(

				AND s.id = #{id_site}
			) AS totalError
