mapper.select:
SELECT d.id, d.datatablename, s.commissioning FROM device d LEFT JOIN site s ON s.id = d.id_site WHERE d.id_site = #{id} AND d.status = 1 AND d.is_delete = 0
AND d.is_excluded_meter = 0
#{id}
#{id}
AND FALSE
AND s.status = 1 AND s.is_delete = 0 AND (CONVERT_TZ(UTC_TIMESTAMP(), 'UTC', #{timezone_value}) < s.expiration OR s.expiration IS NULL)

mapper.sql:
SELECT '${interval}' AS categories_time, IFNULL(SUM(`value`), 0) AS `value` FROM (
SELECT ma.${reading_field} - mi.${reading_field} AS `value` FROM ( SELECT dv.${reading_field}, dv.time FROM ${item.datatablename} dv WHERE dv.time = ( SELECT MIN(dv.time) AS time FROM ${item.datatablename} dv WHERE DATE_FORMAT(CONVERT_TZ(dv.time, 'UTC', #{timezone_value}), '${dateTimeFormat}') = DATE_FORMAT(DATE_SUB(CONVERT_TZ(UTC_TIMESTAMP(), 'UTC', #{timezone_value}), INTERVAL ${beforeDateTimeValue} ${dateTimeUnit}), '${dateTimeFormat}') ) ) mi CROSS JOIN ( SELECT dv.${reading_field}, dv.time FROM ${item.datatablename} dv WHERE dv.time = ( SELECT ${aggregate_function}(dv.time) AS time FROM ${item.datatablename} dv WHERE DATE_FORMAT(CONVERT_TZ(dv.time, 'UTC', #{timezone_value}), '${dateTimeFormat}') = DATE_FORMAT(DATE_SUB(CONVERT_TZ(UTC_TIMESTAMP(), 'UTC', #{timezone_value}), INTERVAL ${afterDateTimeValue} ${dateTimeUnit}), '${dateTimeFormat}') ) ) ma
) t

mapper.select:
SELECT time_full, categories_time, CAST(SUM(`value`) AS DECIMAL) AS `value` FROM (
SELECT mi.time, mi.time_format, mi.time_full, mi.categories_time, IFNULL(LEAD(mi.${reading_field}) OVER (ORDER BY mi.time_format), ma.${reading_field}) - mi.${reading_field} AS `value` FROM (
) mi LEFT JOIN (
) ma ON ma.time_full = mi.time_full
) t WHERE time BETWEEN CONVERT_TZ(#{start_date}, #{timezone_value}, 'UTC') AND CONVERT_TZ(#{end_date}, #{timezone_value}, 'UTC') GROUP BY time_format

SELECT l.id, CONCAT(d.devicename, " ", e.message) AS event, d.devicename AS hardware, i.icon AS icon_url, DATE_FORMAT(CONVERT_TZ(l.start_date, 'UTC', #{timezone_value}), '%d %b, %Y %h:%i %p') AS trigger_date, IF( TIMESTAMPDIFF(YEAR, l.start_date, UTC_TIMESTAMP()) > 0, CONCAT(TIMESTAMPDIFF(YEAR, l.start_date, UTC_TIMESTAMP()), " year", IF(TIMESTAMPDIFF(YEAR, l.start_date, UTC_TIMESTAMP()) > 1, "s", "")), IF( TIMESTAMPDIFF(MONTH, l.start_date, UTC_TIMESTAMP()) > 0, CONCAT(TIMESTAMPDIFF(MONTH, l.start_date, UTC_TIMESTAMP()), " month", IF(TIMESTAMPDIFF(MONTH, l.start_date, UTC_TIMESTAMP()) > 1, "s", "")), IF( TIMESTAMPDIFF(DAY, l.start_date, UTC_TIMESTAMP()) > 0, CONCAT(TIMESTAMPDIFF(DAY, l.start_date, UTC_TIMESTAMP()), " day", IF(TIMESTAMPDIFF(DAY, l.start_date, UTC_TIMESTAMP()) > 1, "s", "")), IF( TIMESTAMPDIFF(HOUR, l.start_date, UTC_TIMESTAMP()) > 0, CONCAT(TIMESTAMPDIFF(HOUR, l.start_date, UTC_TIMESTAMP()), " hour", IF(TIMESTAMPDIFF(HOUR, l.start_date, UTC_TIMESTAMP()) > 1, "s", "")), IF( TIMESTAMPDIFF(MINUTE, l.start_date, UTC_TIMESTAMP()) > 0, CONCAT(TIMESTAMPDIFF(MINUTE, l.start_date, UTC_TIMESTAMP()), " minute", IF(TIMESTAMPDIFF(MINUTE, l.start_date, UTC_TIMESTAMP()) > 1, "s", "")), CONCAT(TIMESTAMPDIFF(SECOND, l.start_date, UTC_TIMESTAMP()), " second", IF(TIMESTAMPDIFF(SECOND, l.start_date, UTC_TIMESTAMP()) > 1, "s", "")) ) ) ) ) ) AS opened_period FROM `alert` l LEFT JOIN error e ON e.id = l.id_error LEFT JOIN error_level el ON el.id = e.id_error_level LEFT JOIN icon i ON i.id = el.id_icon LEFT JOIN device d ON d.id = l.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE e.status = 1 AND e.is_delete = 0 AND l.status = 1 AND l.is_delete = 0
AND CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) BETWEEN #{start_date} AND #{end_date}
AND s.enable_alert = 1
#{item.id}
ORDER BY id DESC
